<script>
import Axios from 'axios'
export default {
  data() {
    return {
      email: ''
    }
  },
  methods: {
    async submitSignUp() {
      const options = {
        method: 'POST',
        crossDomain: true,
        headers: {
          'content-type': 'application/x-www-form-urlencoded',
          'cache-control': 'no-cache'
        },
        url: 'https://manage.kmail-lists.com/ajax/subscriptions/subscribe ',
        data: {
          g: 'MSYZ4c',
          email: this.email
        }
      }

      await Axios(options)
    }
  }
}
