<script>
import debounce from 'lodash.debounce'
import MainNavCart from '~/components/nacelle/MainNavCart'
import MainNavBurger from '~/components/nacelle/MainNavBurger'
import Axios from 'axios'
import Cart from '~/components/nacelle/Cart'
import { mapState, mapMutations } from 'vuex'

import customerChat from '~/mixins/customerChat'
import optimonk from '~/mixins/optimonk'

export default {
  data() {
    return {
      routeName: '',
      screenWidth: 0,
      logoFill: '#373975',
      jetpackMenuVisible: false,
      freeShippingMessage: 'FREE EXPRESS SHIPPING',
      navColor: 'white',
      scrollY: 0,
      scrollTarget: 200,
      headerClass: 'bg-transparent',
      isIndex: true,
      userwayScriptLoaded: false,
      userwayLoading: false,
      userwayFirstTime: true
    }
  },
  mounted() {
    if (process.client) {
      this.screenWidth = screen.width
      if (this.$route.name === 'index') {
        this.handleScroll()
        this.mountScrollWatch()
        this.isIndex = true
      } else {
        this.headerClass = 'bg-white'
        this.navColor = 'black'
        this.isIndex = false
      }
    }

    this.getLocale()
  },
  components: {
    Cart,
    MainNavCart,
    MainNavBurger
  },
  props: {
    isSticky: {
      type: Boolean,
      default: true
    }
  },
  mixins: [customerChat, optimonk],
  watch: {
    '$nuxt.$route.name'(value) {
      this.routeName = value
      console.log('route', this.routeName)
      if (this.routeName === 'index') {
        this.navColor = 'white'
        this.handleScroll()
        this.mountScrollWatch()
        this.isIndex = true
      } else {
        this.navColor = '#373975'
        this.logoFill = '#373975'
        this.headerClass = 'bg-white'
        this.isIndex = false
        this.removeScrollWatch()
      }
    },
    $route() {
      this.closeMenu()
    }
  },
  computed: {
    ...mapState('cart', ['cartVisible']),
    ...mapState('space', ['id', 'name', 'linklists']),
    ...mapState('menu', ['menuVisible']),
    mainNavClass() {
      return {
        // 'main-nav': true,
        // 'main-nav__initial': !this.scrolled,
        // 'main-nav__scrolled': this.scrolled
      }
    },
    logoHeight() {
      if (this.screenWidth > 768) {
        return 38
      } else {
        return 30
      }
    }
  },
  methods: {
    ...mapMutations('menu', ['disableMenu', 'closeMenu', 'toggleShowMenu']),
    handleScroll(event) {
      this.scrollY = window.scrollY
      if (this.scrollY > this.scrollTarget) {
        this.logoFill = '#373975'
        this.navColor = 'black'
        this.headerClass = 'bg-white'
      } else {
        this.logoFill = 'white'
        this.navColor = 'white'
        this.headerClass = 'bg-transparent'
      }
    },
    loadUserwayScript() {
      this.userwayLoading = true
      if (this.userwayScriptLoaded) {
        if (!this.userwayFirstTime) {
          this.userwayLoading = false
        }
        setTimeout(() => {
          this.userwayLoading = false
        }, 3000)
        return
      }
      var s = document.createElement('script')
      s.setAttribute('data-trigger', 'accessibilityWidget')
      s.setAttribute('data-account', 'rGS1KpZP6i')
      s.setAttribute('src', 'https://cdn.userway.org/widget.js')
      ;(document.body || document.head).appendChild(s)
      s.onload = () => {
        console.log('script loaded')
        setTimeout(() => {
          this.$refs.userway.click()
        }, 2000)
        setTimeout(() => {
          this.userwayFirstTime = false
        }, 5000)
      }
      this.userwayScriptLoaded = true
    },
    async getLocale() {
      const price = encodeURIComponent(JSON.stringify([{ Price: 1 }]))
      const config = {
        method: 'get',
        url: `https://checkout.gointerpay.net/v2.21/localize?MerchantId=3af65681-4f06-46e4-805a-f2cb8bdaf1d4&MerchantPrices=${price}`
      }
      await Axios(config)
        .then(res => {
          if (res.data.Country !== 'US') {
            this.freeShippingMessage = 'FREE WORLDWIDE SHIPPING'
            localStorage.setItem('ww', 'true')
          } else {
            localStorage.setItem('ww', 'false')
          }
        })
        .catch(res => {
          console.error('Currency Request Failed', res)
        })
    },
    mountScrollWatch() {
      this.handleDebouncedScroll = debounce(this.handleScroll, 0)
      window.addEventListener('scroll', this.handleDebouncedScroll, {
        passive: true
      })
    },
    handleCloseMenu() {
      this.closeMenu()
      this.showCustomerChat('navbar')
      this.showOptimonkPopup('navbar')
    },
    handleToggleShowMenu() {
      this.toggleShowMenu()
      this.showCustomerChat('navbar')
      this.showOptimonkPopup('navbar')
    },
    removeScrollWatch() {
      window.removeEventListener('scroll', this.handleDebouncedScroll)
    },
    toggleJetpackMenu() {
      this.jetpackMenuVisible = !this.jetpackMenuVisible
    },
    handleJetpackMenu() {
      if (this.jetpackMenuVisible) {
        this.jetpackMenuVisible = !this.jetpackMenuVisible
      }
    }
  }
}
</script>

